pragma solidity ^0.4.18;
/**
 * @title Recorder — record a message into the blockchain
 * @author Life on Mars — https://lifeonmars.pt
 */
contract Recorder{

  //only the machine should put records
  struct EnergyRecord {
    uint userId;
    uint machineId;
    uint wattHour;
  }


  EnergyRecord[] public records;


  function createRecord(uint userId,uint machineId, uint wattHour) public returns (uint) {
      EnergyRecord storage newRecord;
      newRecord.userId = userId;
      newRecord.machineId = machineId;
      newRecord.wattHour = wattHour;
      records.push(newRecord);
      return records.length;
  }

  function getRecords() public returns (uint[],uint[],uint[]){

    uint[] memory userId = new uint[](records.length);
    uint[] memory machineId = new uint[](records.length);
    uint[] memory wattHour = new uint[](records.length);

    for (uint i = 0; i < records.length; i++){
      userId[i] = records[i].userId;
      machineId[i] = records[i].machineId;
      wattHour[i] = records[i].wattHour;
    }
    return(userId,machineId,wattHour);
  }
}
